    // }

    // handleChange = (e) =>{
    //     let {name, value} = e.target
    //     this.setState({
    //         [name]:value
    //     })
    // }





    render() {
        console.log(this.props.handleSubmit(this.state));
        let {formName} = this.props
        let {username, password} = this.state

        return (
            <Fragment>
                {/* LEFT SIDE OF THE PAGE */}
                <div className='appAside'>

                        <h1>POKEMON</h1>


                </div>

                {/* RIGHT SIDE OF THE PAGE */}
                <div className='appForm'>
                    {/* DIV FOR THE TOGGLE BUTTONS ON TOP OF PAGE */}
                    <div className='pageSwitch'>
                        <a href='#' className='toggleSwitchActive'> Sign In</a>
                        <a href='#' className='toggleSwitch'> Sign Up</a>
                    </div>
                    {/* START OF THE FORM */}
                    <div className='login100-form'>
                        <form onSubmit={this.handleSubmit}>
                            <input className='input100' type='text' id='username' name='username' placeholder='Username' value={username} onChange={this.handleChange}></input>
                            <input className='input100' type='password' id='password' name='password' placeholder='Password' value={password}></input>
                            <input type='submit' value='Submit'/>
                            <button className='loginButton' type='submit'>Login</button>
                        </form>
                    </div>

                </div>
            </Fragment>
        )
    }
}

export default LoginForm
